      handlePopover(oEvent, oView, fragmentName, popoverKey) {
          if (!this[popoverKey]) {
              this[popoverKey] = Fragment.load({
                  id: oView.getId(),
                  name: fragmentName,
                  controller: oView.getController()
              })
              .then(function (oPopover) {
