              oModel.setProperty("/contrastConfig/previewBackgroundColor", backgroundColor);
          }
          if (textColor) {
              oText.getDomRef().style.color = textColor;
              oModel.setProperty("/contrastConfig/previewTextColor", textColor);
          }
      }

